    function _sumMultiTrendDatas(dataList) {
        // 将多个趋势的值进行求和
        if (!dataList.length) {
            return [];
        }
        var values = [];
        var len = dataList[0].length;
        for (var i = 0; i < len; i++) {
            var total = null;
            dataList.forEach(function (datas) {
                if (total === null && datas[i] !== null) {
                    total = datas[i];
                } else if (total !== null && datas[i] !== null) {
                    total += datas[i];
                }
            });
            if (total === null) {
                values.push(total);
            } else {
                values.push(parseFloat(total.toFixed(2)));
            }
        }
        return values;
    }

    var chart5 = null;
    function createDegreeTrend() {
        // 用能趋势
        var sns = [],
            showItems = [];
        energyItems.forEach(function (item) {
            if (item.level === 1) {
                item.deviceVarSns.forEach(function (sn) {
                    if (sns.indexOf(sn) < 0) {
                        sns.push(sn);
                    }
                });
                showItems.push(item);
            }
        });
        var queryPeriod = '',
            startTime,
            endTime;
        switch (timeType) {
            case 'DAY':
                startTime = currentDate + ' 00:00:00.000';
                endTime = currentDate + ' 23:59:59.000';
                queryPeriod = 'HOUR';
                break;
            case 'MONTH':
                startTime = currentDate + '-01 00:00:00.000';
                endTime = moment(currentDate, 'YYYY-MM').add(1, 'month').set('date', 1).subtract(1, 'day').format('YYYY-MM-DD 23:59:59.000');
                queryPeriod = 'DAY';
                break;
            case 'YEAR':
                startTime = currentDate + '-01-01 00:00:00.000';
                endTime = currentDate + '-12-31 23:59:59.000';
                queryPeriod = 'MONTH';
                break;
        }
        var timeKeys = createTimeList(startTime, endTime, queryPeriod, 'YYYY-MM-DD HH:mm:ss.000');
        var times = [];
        timeKeys.forEach(function (t) {
            switch (timeType) {
                case 'DAY':
                    times.push(parseInt(t.substring(11, 13)) + '时');
                    break;
                case 'MONTH':
                    times.push(parseInt(t.substring(8, 10) + '日'));
                    break;
                case 'YEAR':
                    times.push(parseInt(t.substring(5, 7)) + '月');
                    break;
            }
        });
        $scope.loadingStatus['5'] = true;
        fetchElectricTrend(sns, startTime, endTime, queryPeriod, function (datas) {
            $scope.loadingStatus['5'] = false;
            $scope.$apply();
            _paint(times, datas);
        });

        function _paint(times, datas) {
            var seriesData = [],
                labels = [];
            showItems.forEach(function (item) {
                var dataList = [];
                distinctVarSns(item.deviceVarSns).forEach(function (sn) {
                    datas.forEach(function (data) {
                        if (data.var.sn === sn) {
                            dataList.push(data.datas);
                            return false;
                        }
                    });
                });
                var sumedDatas = _sumMultiTrendDatas(dataList);
                seriesData.push({
                    name: item.aliasName,
                    type: 'bar',
                    stack: '总量',
                    barMaxWidth: "40px",
                    data: sumedDatas
                });
                labels.push(item.aliasName);
            });
            if (!seriesData.length) {
                seriesData.push({
                    name: '',
                    type: 'bar',
                    stack: '总量',
                    data: []
                });
            }
            var config = {
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {
                        type: 'shadow'
                    }
                },
                legend: {
                    data: labels,
                    bottom: 0,
                    textStyle: {
                        fontSize: 9,
                        rich: {
                            fonSize: 9
                        }
                    }
                },
                grid: {
                    top: 20,
                    left: 20,
                    right: 10,
                    bottom: 30,
                    containLabel: true
                },
                toolbox: {
                    feature: {
                        saveAsImage: false
                    }
                },
                xAxis: {
                    type: 'category',
                    data: times
                },
                yAxis: {
                    type: 'value',
                    name: 'kWh',
                    nameGap: '5'
                },
                color: ENERGY_LINE_COLORS,
                series: seriesData
            };
            if (chart5) {
                chart5.dispose();
            }
            chart5 = echarts.init(document.getElementById('chart_bar4'));
            chart5.setOption(config);
        }
    }
