app.controller('LoginCtrl', ['$scope', '$timeout', 'platformService', 'userService', '$state', '$http', 'ajax', function ($scope, $timeout, platformService, userService, $state, $http, ajax) {
    $scope.error = '';
    var platform = null;
    $scope.enable = false;
    $scope.platformCodeVisible = !defaultPlatIpAddr;
    $scope.platformCode = platformService.getLatestPlatform() ? platformService.getLatestPlatform().code : null;
    $scope.username = userService.getUsername();
    $scope.password = userService.getPassword();
    $scope.isLogin = false;
    $scope.isAutoLogin = false;
    $scope.passwordVisible = false;

    $scope.togglePasswordVisible = function () {
        $scope.passwordVisible = !$scope.passwordVisible;
    };

    $scope.inputChange = function () {
        if ((!$scope.platformCodeVisible || $scope.platformCode) && $scope.username && $scope.password) {
            $scope.enable = true;
        } else {
            $scope.enable = false;
        }
    }; // 输入更改事件
    $scope.inputChange();

    $scope.login = function () {
        $scope.enable = false;
        if (defaultPlatIpAddr) {
            login();
        } else {
            queryPlatform(login);
        }
    };

    $scope.setAutoLogin = function (isAutoLogin) {
        $scope.isAutoLogin = isAutoLogin;
    };

    function toast(message) {
        var div = $('<div class="toast"><div class="msg">' + message + '</div></div>').appendTo($('body'));
        $timeout(function () {
            div.remove();
        }, 2000);
    }

    function login() {
        $scope.error = '';
        var data = {
            username: $scope.username,
            password: $scope.password
        };
        var loginUrl = platformService.getAuthHost();
        $scope.isLogin = true;
        $.ajax({
            url: loginUrl + '/auth',
            method: 'POST',
            timeout: 10000,
            data: JSON.stringify(data),
            dataType: "json",
            headers: {
                "xhrFields-Type": {
                    withCredentials: true
                },
                'Content-Type': 'application/json;chartset=UTF-8'
            },
            xhrFields: {
                withCredentials: true
            },
            crossDomain: true,
            success: function success(data) {
                var result = KJUR.jws.JWS.verify(data.token, 'zjlhstest');
                if (result) {
                    if (!getStorageItem('latestPlatform')) {
                        platformService.setLatestPlatform({ url: defaultPlatIpAddr + ":8099/v1" });
                    }
                    userService.setAccountToken(data.token);
                    getUserInfo();
                } else {
                    toast('用户名或密码错误');
                    if ($scope.isAutoLogin) {
                        userService.setPassword('');
                        location.href = 'login.html';
                    }
                }
            },
            error: function error() {
                $scope.enable = true;
                $scope.isLogin = false;
                $scope.$apply();
                toast('用户名或密码错误');
                if ($scope.isAutoLogin) {
                    userService.setPassword('');
                    location.href = 'login.html';
                }
            }
        });
    }

    function getUserInfo() {
        ajax.get({
            url: '/user/' + $scope.username,
            ignoreAuthExpire: true,
            success: function success(data) {
                userService.saveLoginUser(data, $scope.password);
                // getCompany();
                if (window.android) {
                    window.android.loginSuccess();
                } else {
                    location.href = '/templates/home.html?finishPage=1';
                }
            },
            error: function error() {
                $scope.enable = true;
                $scope.isLogin = false;
                toast('用户名或密码错误');
                $scope.$apply();
            }
        });
    }

    // 平台查询start
    $scope.platformError = '';
    function queryPlatform(cb) {
        $scope.platformError = '';

        $http({
            url: gPublicApiHost + '/v1/sites/' + $scope.platformCode,
            xhrFields: {
                withCredentials: true
            },
            crossDomain: true,
            timeout: 10000
        }).then(function (data) {
            platform = data.data;
            if (!platform) {
                if ($scope.isAutoLogin) {
                    userService.setPassword('');
                    location.href = 'login.html';
                } else {
                    $scope.enable = true;
                    toast('平台编号错误');
                    $scope.$apply();
                }
                return;
            }
            // platform.url = 'http://127.0.0.1:8099/v1';
            platformService.setLatestPlatform(platform);
            cb && cb();
        }).catch(function () {
            if ($scope.isAutoLogin) {
                userService.setPassword('');
                location.href = '/templates/login.html';
                $scope.enable = false;
            } else {
                $scope.enable = true;
                toast('平台编号错误');
                $scope.$apply();
            }
        });
    }
    // 平台查询end
}]);

