                $scope.device_record.forEach(function (r) {
                    if (r.checked) {
                        r.status = '1';
                        r.status_name = '正常';
                        r.className = "normal";
                        $scope.checkDevice(null, r.device_sn);
                    }
                });
                $scope.toggleCheckAll(false);
                $scope.recountFunc();
                $scope.$apply();
