var scrollTop = 0;
var jic = {
    /**
     * Receives an Image Object (can be JPG OR PNG) and returns a new Image Object compressed
     * @param {Image} source_img_obj The source Image Object
     * @param {Integer} quality The output quality of Image Object
     * @return {Image} result_image_obj The compressed Image Object
     */

    readFileAsDataURL: function readFileAsDataURL(file, callback) {
        var a = new FileReader();
        a.onload = function (e) {
            callback(e.target.result);
        };
        a.readAsDataURL(file);
    },
    //对图片旋转处理 added by lzk www.bcty365.com
    _rotateImg: function _rotateImg(img, direction, canvas) {
        //alert(img);
        //最小与最大旋转方向，图片旋转4次后回到原方向
        var min_step = 0,
            max_step = 3;
        //var img = document.getElementById(pid);
        if (img === null) return;
        //img的高度和宽度不能在img元素隐藏后获取，否则会出错
        var originHeight = img.height,
            originWidth = img.width,
            width = originWidth,
            height = originHeight;
        if (originWidth > originHeight) {
            if (originHeight > 2048) {
                height = 2048;
                width = parseInt(height / originHeight * originWidth);
            }
        } else {
            if (originWidth > 2048) {
                width = 2048;
                height = parseInt(width / originWidth * originHeight);
            }
        }
        //var step = img.getAttribute('step');
        var step = 2;
        if (step === null) {
            step = min_step;
        }
        if (direction === 'right') {
            step++;
            //旋转到原位置，即超过最大值
            step > max_step && (step = min_step);
        } else {
            step--;
            step < min_step && (step = max_step);
        }
        //旋转角度以弧度值为参数
        var degree = step * 90 * Math.PI / 180,
            ctx = canvas.getContext('2d');
        switch (step) {
            case 0:
                canvas.width = width;
                canvas.height = height;
                ctx.drawImage(img, 0, 0);
                break;
            case 1:
                canvas.width = height;
                canvas.height = width;
                ctx.rotate(degree);
                ctx.drawImage(img, 0, -height);
                break;
            case 2:
                canvas.width = width;
                canvas.height = height;
                ctx.rotate(degree);
                ctx.drawImage(img, -width, -height);
                break;
            case 3:
                canvas.width = height;
                canvas.height = width;
                ctx.rotate(degree);
                ctx.drawImage(img, -width, 0);
                break;
        }
    },
    compress: function compress(source_img_obj, quality, orientation) {
        var that = this,
            mime_type = "image/jpeg",
            cvs = document.createElement('canvas');
        //naturalWidth真实图片的宽度
        var originWidth = source_img_obj.naturalWidth,
            originHeight = source_img_obj.naturalHeight,
            width = originWidth,
            height = originHeight;
        if (orientation && orientation !== 1) {
            switch (orientation) {
                case 6:
                    //需要顺时针（向左）90度旋转
                    that._rotateImg(source_img_obj, 'left', cvs);
                    break;
                case 8:
                    //需要逆时针（向右）90度旋转
                    that._rotateImg(source_img_obj, 'right', cvs);
                    break;
                case 3:
                    //需要180度旋转
                    that._rotateImg(source_img_obj, 'right', cvs); //转两次
                    that._rotateImg(source_img_obj, 'right', cvs);
                    break;
            }
        } else {
            if (originWidth > originHeight) {
                if (originHeight > 2048) {
                    height = 2048;
                    width = parseInt(height / originHeight * originWidth);
                }
            } else {
                if (originWidth > 2048) {
                    width = 2048;
                    height = parseInt(width / originWidth * originHeight);
                }
            }
            cvs.width = width;
            cvs.height = height;
            cvs.getContext("2d").drawImage(source_img_obj, 0, 0, originWidth, originHeight, 0, 0, width, height);
        }
        var newImageData = cvs.toDataURL(mime_type, quality / 100),
            result_image_obj = new Image();
        result_image_obj.src = newImageData;
        return result_image_obj;
    },
    upload: function upload(basestr, type, $li, formArray, successCallback) {
        var text = window.atob(basestr.split(",")[1]);
        var buffer = new ArrayBuffer(text.length);
        var ubuffer = new Uint8Array(buffer);
        var pecent = 0,
            loop = null;

        for (var i = 0; i < text.length; i++) {
            ubuffer[i] = text.charCodeAt(i);
        }

        var Builder = window.WebKitBlobBuilder || window.MozBlobBuilder;
        var blob;

        if (Builder) {
            var builder = new Builder();
            builder.append(buffer);
            blob = builder.getBlob(type);
        } else {
            blob = new window.Blob([buffer], { type: type });
        }

        var xhr = new XMLHttpRequest();
        var formdata = new FormData();
        formdata.append('images', blob);
        $.each(formArray, function (i, n) {
            formdata.append(n['name'], n['value']);
        });

        xhr.open('post', '/uploads');

        xhr.onreadystatechange = function () {
            if (xhr.readyState == 4 && xhr.status == 200) {
                console.log('上传成功：');
                clearInterval(loop);
                //当收到该消息时上传完毕
                $li.find(".js_progress").animate({ 'width': "100%" }, pecent < 95 ? 200 : 0);
                successCallback && successCallback(xhr.responseText);
            }
        };

        //数据发送进度，前50%展示该进度
        xhr.upload.addEventListener('progress', function (e) {
            if (loop) return;
            //console.log('loaded: ' + e.loaded + ', total: ' + e.total);
            //pecent = ~~(100 * e.loaded / e.total) / 2;
            pecent = parseInt(e.loaded / e.total * 100);
            console.log(pecent);
            $li.find(".js_progress").css('width', pecent + "%");

            // if (pecent > 85) {
            //     mockProgress();
            // }
        }, false);

        //数据后90%用模拟进度
        function mockProgress() {
            if (loop) return;

            loop = setInterval(function () {
                pecent++;
                $li.find(".js_progress").css('width', pecent + "%");

                if (pecent == 99) {
                    clearInterval(loop);
                }
            }, 100);
        }

        xhr.send(formdata);
    }
};

